import React from 'react';
import './HowItWorks.css';

function HowItWorks() {
  return (
    <section className="how-it-works">
      <div className="container">
        <h2>How Communicat Helps You Get It Right</h2>
        <ol>
          <li>Start a Chat – Message just like any other chat app.</li>
          <li>Send a Message – Type what you really feel. Our AI watches for harmful patterns.</li>
          <li>Get Feedback (Not Judgment) – An evaluation appears discreetly—telling you if your words may cause harm.</li>
          <li>Use the Cooldown If Needed – Hit the 'Cool Down' button and let the heat pass. It's built-in protection.</li>
          <li>Grow Together – Less blaming. More listening. Stronger conversations.</li>
        </ol>
      </div>
    </section>
  );
}

export default HowItWorks;
