import React from 'react';
import './ProblemSection.css';

function ProblemSection() {
  return (
    <section className="problem-section">
      <div className="container">
        <h2>Why Do the People Closest to Us Get the Worst of Us?</h2>
        <p>Misunderstandings, harsh words, silent treatments—sometimes it feels like every message makes things worse. Most messaging apps help you send. Communicat helps you connect. Because healthy relationships don't come with a guidebook—until now.</p>
      </div>
    </section>
  );
}

export default ProblemSection;
